import React from 'react';
import NewMessageForm from './NewMessageForm';

const MessagesArea = (props) => {
  const {
    chatroom: { id, name, messages }, user_id, cableApp
  } = props;
  const [messageList, setMessageList] = React.useState(messages)

  React.useEffect(() => {
    console.log(props);
    console.log(messages);
    console.log(cableApp);
    cableApp.room = cableApp.cable.subscriptions.create({
        channel: 'chatroom_channel',
        chatroom: id
    }, 
    {
        received: (newMessage) => {
          console.log(newMessage);
          const allMessages = messageList;
          allMessages.push(newMessage);
          console.log('allMessages', allMessages);
          setMessageList(allMessages)
        }
    })
  })

  const orderedMessages = messages => {
    const sortedMessages = messages.sort(
      (a, b) => new Date(a.created_at) - new Date(b.created_at)
    );
    return sortedMessages.map(message => {
      return <li key={message.id}>{message.body}</li>;
    });
  };

  console.log('messageList', messageList);
  return (
    <div className="messagesArea">
      <h2>{name}</h2>
      <ul>{orderedMessages(messageList)}</ul>
      <NewMessageForm chatroom_id={id} user_id={user_id} />
    </div>
  );
};

export default MessagesArea;
