import React from 'react';
import NewMessageForm from './NewMessageForm';

const MessagesArea = (props) => {
  console.log(props.chatroom, props.chatroom.messages);
  const [messageList, setMessageList] = React.useState();

  const updateMessages = (message) => {
    console.log(messageList.concat(message));
    let allMessages = messageList;
    console.log('allMessages before', allMessages);
    setMessageList(messageList.concat(message))
  }

  React.useEffect(() => {
    setMessageList(props.chatroom && props.chatroom.messages)
    props.cableApp.room = props.cableApp.cable.subscriptions.create({
        channel: 'chatroom_channel',
        chatroom: props.id
    }, 
    {
        received: (newMessage) => {
          updateMessages(newMessage);
        }
    })
  },[messageList])

  const orderedMessages = messages => {
    if (!messages) return <li>No Messages yet...</li>
    const sortedMessages = messages.sort(
      (a, b) => new Date(a.created_at) - new Date(b.created_at)
    );
    return sortedMessages.map(message => {
      return <li key={message.id}>{message.body}</li>;
    });
  };

  console.log('messageList', messageList);
  return (
    <div className="messagesArea">
      <h2>{props.chatroom && props.chatroom.name}</h2>
      <ul>{orderedMessages(messageList)}</ul>
      <NewMessageForm chatroom_id={props.chatroom && props.chatroom.id} user_id={props.user_id} />
    </div>
  );
};

export default MessagesArea;
