  state = {
    // chatrooms: [],
    activeChatroom: {}//this.props.chatroom
  };

  componentWillMount = () => {
    const { chatroom } = this.props;
    if (!chatroom.messages) chatroom.messages = [];
    if (!chatroom.id) chatroom.id = 1;
    axios.get(`http://localhost:3003/chatrooms/${chatroom.id}`)
    .then(result => {
      if (result && result.data) {
        this.setState({activeChatroom: result.data});
      }
    })
    
    // this.postFirstMessage(chatroom.id);
