    if (!activeChatroom.messages) activeChatroom.messages = [];
    // activeChatroom.messages.concat(message)
    activeChatroom.messages = [...activeChatroom.messages, message];
    this.setState({ activeChatroom });
  };

  // postFirstMessage = (roomId) => {
